import classNames from "classnames"
import { useRef } from "react"
import { InputCheckboxComponent } from "./types"

export const InputCheckbox: InputCheckboxComponent = ({ id, checked, disabled, onChangeSomeState }) => {
  const { current: inputId } = useRef(`RampInputCheckbox-${id}`)

  /*
  async function onChangeSomeState(newValue: boolean) {
    console.log("onChangeSomeState")
    // console.log(newValue)
    console.log("before in onChangeSomeState", approved)
    await consumerSetTransactionApproval({
      transactionId: transaction.id,
      newValue,
    })
    setApproved(newValue)
    console.log("after in onChangeSomeState", approved)
  }
  */
  async function handleChangeStateClick() {
    await onChangeSomeState(!checked)
  };

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,
        })}
        onClick={handleChangeStateClick}
      />
      <input
        id={inputId}
        type="checkbox"
        className="RampInputCheckbox--input"
        checked={checked}
        disabled={disabled}
        onChange={() => {
        }}
      />
    </div>
  )
}
